import React from "react";

interface CardProps {
  title: string;
  description: string;
  actionText: string;
  icon: React.ReactNode;
}

const Card: React.FC<CardProps> = ({ title, description, actionText, icon }) => {
  return (
    <div
      className={css({
        flex: 1,
        display: "flex",
        flexDirection: "column",
        gap: "20px",
        backgroundColor: "rgba(245, 230, 212, 0.5)", // Lighter desert tone
        padding: "28px",
        borderRadius: "16px",
        minWidth: { base: "100%", medium: "0" },
        transition: "transform 0.2s ease",
        _hover: {
          transform: "translateY(-2px)",
        },
      })}
    >
      <div>
        {icon}
      </div>
      <div
        className={css({
          display: "flex",
          flexDirection: "column",
          gap: "12px",
        })}
      >
        <h3
          className={css({
            fontSize: "24px",
            fontWeight: "700",
            color: "#402108", // Dark brown for contrast
            lineHeight: "1.2",
          })}
        >
          {title}
        </h3>
        <p
          className={css({
            fontSize: "16px",
            color: "#804e13", // Medium brown for readability
            lineHeight: "1.5",
          })}
        >
          {description}
        </p>
      </div>
      <a
        href="#"
        className={css({
          alignSelf: "flex-start",
          fontSize: "16px",
          fontWeight: "600",
          color: "#804e13", // Medium brown
          textDecoration: "underline",
          cursor: "pointer",
          _hover: {
            color: "#402108", // Darker on hover
          },
        })}
      >
        {actionText}
      </a>
    </div>
  );
};

